<style>
	[id$='button'] {
		cursor: pointer;
	}

	[id$='button']:hover + [id$='logo'],
	[id$='button']:hover + [id$='logo'] path {
		fill: var(--c-g-dark);
	}

	[id$='about-button'] {
		animation: 1500ms ease-in-out 2 highlight-purple;
	}

	[id$='about-button'] + [id$='logo'] {
		animation: 1500ms ease-in-out 2 lowlight;
	}

	[id$='about-button']:hover {
		stroke: var(--c-purple);
		fill: var(--c-purple);
	}

	[id$='about-button']:active {
		stroke: var(--c-purple);
		fill: var(--c-g-dark);
	}

	[id$='about-button']:active + [id$='logo'],
	[id$='about-button']:active + [id$='logo'] path {
		fill: var(--c-purple);
	}

	[id$='projects-button'] {
		animation: 1500ms ease-in-out 2 highlight-red;
		animation-delay: 200ms;
	}

	[id$='projects-button'] + [id$='logo'] {
		animation: 1500ms ease-in-out 2 lowlight;
		animation-delay: 200ms;
	}

	[id$='projects-button']:hover {
		stroke: var(--c-red);
		fill: var(--c-red);
	}

	[id$='projects-button']:active {
		stroke: var(--c-red);
		fill: var(--c-g-dark);
	}

	[id$='projects-button']:active + [id$='logo'],
	[id$='projects-button']:active + [id$='logo'] path {
		fill: var(--c-red);
	}

	[id$='team-button'] {
		animation: 1500ms ease-in-out 2 highlight-yellow;
		animation-delay: 400ms;
	}

	[id$='team-button'] + [id$='logo'] path {
		animation: 1500ms ease-in-out 2 lowlight;
		animation-delay: 400ms;
	}

	[id$='team-button']:hover {
		stroke: var(--c-yellow);
		fill: var(--c-yellow);
	}

	[id$='team-button']:active {
		stroke: var(--c-yellow);
		fill: var(--c-g-dark);
	}

	[id$='team-button']:active + [id$='logo'],
	[id$='team-button']:active + [id$='logo'] path {
		fill: var(--c-yellow);
	}

	[id$='clients-button'] {
		animation: 1500ms ease-in-out 2 highlight-green;
		animation-delay: 600ms;
	}

	[id$='clients-button'] + [id$='logo'] {
		animation: 1500ms ease-in-out 2 lowlight;
		animation-delay: 600ms;
	}

	[id$='clients-button']:hover {
		stroke: var(--c-green);
		fill: var(--c-green);
	}

	[id$='clients-button']:active {
		stroke: var(--c-green);
		fill: var(--c-g-dark);
	}

	[id$='clients-button']:active + [id$='logo'],
	[id$='clients-button']:active + [id$='logo'] path {
		fill: var(--c-green);
	}

	[id$='contact-button'] {
		animation: 1500ms ease-in-out 2 highlight-blue;
		animation-delay: 800ms;
	}

	[id$='contact-button'] + [id$='logo'] {
		animation: 1500ms ease-in-out 2 lowlight;
		animation-delay: 800ms;
	}

	[id$='contact-button']:hover {
		stroke: var(--c-blue);
		fill: var(--c-blue);
	}

	[id$='contact-button']:active {
		stroke: var(--c-blue);
		fill: var(--c-g-dark);
	}

	[id$='contact-button']:active + [id$='logo'],
	[id$='contact-button']:active + [id$='logo'] path {
		fill: var(--c-blue);
	}

	[id$='channels-minus-button'] {
		animation: 1500ms ease-in-out 2 highlight-pink;
		animation-delay: 1000ms;
	}

	[id$='channels-minus-button'] + [id$='logo'] {
		animation: 1500ms ease-in-out 2 lowlight;
		animation-delay: 1000ms;
	}

	[id$='channels-minus-button']:hover {
		stroke: var(--c-pink);
		fill: var(--c-pink);
	}

	[id$='channels-minus-button']:active {
		stroke: var(--c-pink);
		fill: var(--c-g-dark);
	}

	[id$='channels-minus-button']:active + [id$='logo'],
	[id$='channels-minus-button']:active + [id$='logo'] path {
		fill: var(--c-pink);
	}

	[id$='channels-plus-button'] {
		animation: 1500ms ease-in-out 2 highlight-pink;
		animation-delay: 1200ms;
	}

	[id$='channels-plus-button'] + [id$='logo'] {
		animation: 1500ms ease-in-out 2 lowlight;
		animation-delay: 1200ms;
	}

	[id$='channels-plus-button']:hover {
		stroke: var(--c-pink);
		fill: var(--c-pink);
	}

	[id$='channels-plus-button']:active {
		stroke: var(--c-pink);
		fill: var(--c-g-dark);
	}

	[id$='channels-plus-button']:active + [id$='logo'],
	[id$='channels-plus-button']:active + [id$='logo'] path {
		fill: var(--c-pink);
	}

	@keyframes highlight-purple {
		0% {
			fill: var(--c-g-dark);
		}

		50% {
			fill: var(--c-purple);
		}

		100% {
			fill: var(--c-g-dark);
		}
	}

	@keyframes highlight-red {
		0% {
			fill: var(--c-g-dark);
		}

		50% {
			fill: var(--c-red);
		}

		100% {
			fill: var(--c-g-dark);
		}
	}

	@keyframes highlight-yellow {
		0% {
			fill: var(--c-g-dark);
		}

		50% {
			fill: var(--c-yellow);
		}

		100% {
			fill: var(--c-g-dark);
		}
	}

	@keyframes highlight-green {
		0% {
			fill: var(--c-g-dark);
		}

		50% {
			fill: var(--c-green);
		}

		100% {
			fill: var(--c-g-dark);
		}
	}

	@keyframes highlight-blue {
		0% {
			fill: var(--c-g-dark);
		}

		50% {
			fill: var(--c-blue);
		}

		100% {
			fill: var(--c-g-dark);
		}
	}

	@keyframes highlight-pink {
		0% {
			fill: var(--c-g-dark);
		}

		50% {
			fill: var(--c-pink);
		}

		100% {
			fill: var(--c-g-dark);
		}
	}

	@keyframes lowlight {
		0% {
			fill: var(--c-g-light);
		}

		50% {
			fill: var(--c-g-dark);
		}

		100% {
			fill: var(--c-g-light);
		}
	}
</style>
